#!/bin/bash

cd "$(dirname "$0")"
clear
echo -e "\033[1mTest factordb expression parsing\033[0m"
time ./RsaCtfTool.py --publickey "examples/factordb_parse.pub" --private --attack factordb
echo -e "\033[1m\nTest noveltyprimes\033[0m"
time ./RsaCtfTool.py --publickey examples/elite_primes.pub --private --attack noveltyprimes
echo -e "\033[1m\nTest small_q\033[0m"
time ./RsaCtfTool.py --publickey examples/small_q.pub --private --decryptfile examples/small_q.cipher --attack smallq
echo -e "\033[1m\nTest Mersenne Primes\033[0m"
time ./RsaCtfTool.py --private -e 0x10001 -n  --attack mersenne_primes
echo -e "\033[1m\nTest wiener\033[0m"
time ./RsaCtfTool.py --publickey examples/wiener.pub --private --decryptfile examples/wiener.cipher --attack wiener
echo -e "\033[1m\nTest Boneh Durfee\033[0m"
time ./RsaCtfTool.py --publickey examples/wiener.pub --private --decryptfile examples/wiener.cipher --attack boneh_durfee
echo -e "\033[1m\nTest commonfactors\033[0m"
time ./RsaCtfTool.py --publickey "examples/commonfactor?.pub" --private --attack commonfactors
echo -e "\033[1m\nTest fermat\033[0m"
time ./RsaCtfTool.py --publickey examples/close_primes.pub --private --decryptfile examples/close_primes.cipher --attack fermat
echo -e "\033[1m\nTest fermat2\033[0m"
time ./RsaCtfTool.py --publickey examples/fermat.pub --private --attack fermat
echo -e "\033[1m\nTest pastctfprimes\033[0m"
time ./RsaCtfTool.py --publickey examples/pastctfprimes.pub --private --attack pastctfprimes
echo -e "\033[1m\nTest SIQS\033[0m"
time ./RsaCtfTool.py --publickey examples/siqs.pub --private --attack siqs
echo -e "\033[1m\nTest ECM\033[0m"
time ./RsaCtfTool.py --publickey examples/ecm_method.pub --private --ecmdigits 25 --attack ecm --timeout 120
echo -e "\033[1m\nTest ECM2\033[0m"
time ./RsaCtfTool.py -n  -e 65537 --decrypt  --attack ecm2 --timeout 60
echo -e "\033[1m\nTest createpub\033[0m"
./RsaCtfTool.py --createpub -n 8616460799 -e 65537
echo -e "\033[1m\nCreatepub into Crack feedback\033[0m"
./RsaCtfTool.py --createpub -n  -e 65537 > /tmp/crackme.txt
time ./RsaCtfTool.py --publickey /tmp/crackme.txt --private
rm -f /tmp/crackme.txt
echo -e "\033[1m\nTest hastads\033[0m"
time ./RsaCtfTool.py --publickey "examples/hastads01.pub,examples/hastads02.pub,examples/hastads03.pub" --decrypt ,, --attack hastads
echo -e "\033[1m\nTest ROCA attack\033[0m"
time python3 ./RsaCtfTool.py --attack roca -n 5590772118685579117817112787486780348504267507289026685912623973671010394384988015497235515969796783937905129055952167826830196634107346761087047942625347 -e 65537 --private --timeout 90
echo -e "\033[1m\nTest informations output --dumpkey --ext\033[0m"
time ./RsaCtfTool.py --publickey "examples/factordb_parse.pub" --private --attack factordb --dumpkey --ext
echo -e "\033[1m\nTest decrypting multiple files\033[0m"
time ./RsaCtfTool.py --publickey examples/primefac.pub --decryptfile examples/cipher1,examples/cipher2,examples/cipher3
echo -e "\033[1m\nTest decrypting single file with multiple keys\033[0m"
time ./RsaCtfTool.py --publickey examples/boneh_durfee.pub,examples/primefac.pub  --decryptfile examples/cipher1
echo -e "\033[1m\nTest cube root\033[0m"
time ./RsaCtfTool.py --decrypt 2205316413931134031074603746928247799030155221252519872650101242908540609117693035883827878696406295617513907962419726541451312273821810017858485722109359971259158071688912076249144203043097720816270550387459717116098817458584146690177125 -e 3 -n  --attack cube_root
echo -e "\033[1m\nTest Ekoparty ctf\033[0m"
time ./RsaCtfTool.py --private -e 65537 -n 
echo -e "\033[1m\nTest multiprime from NahamCon ctf\033[0m"
time python3 ./RsaCtfTool.py -n  -e 65537 --decrypt  --attack factordb
echo -e "\033[1m\nTest cm factor\033[0m"
time python3 ./RsaCtfTool.py --publickey examples/cm_factor.pub --attack cm_factor --private
echo -e "\033[1m\nTest pollard rho\033[0m"
time python3 ./RsaCtfTool.py --pub examples/pollard_rho.pub --attack pollard_rho --private --timeout 180
echo -e "\033[1m\nTest fermat numbers gcd\033[0m"
time ./RsaCtfTool.py --publickey examples/fermat_numbers_gcd.pub --attack fermat_numbers_gcd --private
echo -e "\033[1m\nTest mersenne pm1 gcd\033[0m"
time ./RsaCtfTool.py --publickey examples/mersenne_pm1_gcd.pub --attack mersenne_pm1_gcd --private
echo -e "\033[1m\nTest primorial pm1\033[0m"
time ./RsaCtfTool.py --publickey examples/primorial_pm1_gcd.pub --attack primorial_pm1_gcd --private
echo -e "\033[1m\nTest fibonacci gcd\033[0m"
time ./RsaCtfTool.py --publickey examples/fibonacci_gcd.pub --attack fibonacci_gcd --private
echo -e "\033[1m\nTest small crt exponent\033[0m"
time ./RsaCtfTool.py --publickey examples/small_crt_exp.pub --attack small_crt_exp --private
echo -e "\033[1m\nTest to make sure that it does not fail when the list of ciphertexts does not exist\033[0m"
time ./RsaCtfTool.py -n 90377629292003121684002147101760858109247336549001090677693 -e 65537 --sendtofdb --private --timeout 100 --attack factordb
echo -e "\033[1m\nTest same_n_huge_e\033[0m"
time ./RsaCtfTool.py -e 17,65537 -n  --decrypt , --attack same_n_huge_e
echo -e "\033[1,\nTest NSIF Attack\033[0m"
./RsaCtfTool.py -n  --attack nsif -e 69212
echo -e "\033[1,\nTest recover p and q from n,e and d\033[0m"
./RsaCtfTool.py --private -n  -e 65537 -d 